INSERT INTO `wp_comments` VALUES (1, 1, '一位WordPress评论者', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2018-08-01 08:36:22', '2018-08-01 00:36:22', '嗨，这是一条评论。\n要开始审核、编辑及删除评论，请访问仪表盘的“评论”页面。\n评论者头像来自<a href=\"https://gravatar.com\">Gravatar</a>。', 0, '1', '', '', 0, 0);
INSERT INTO `wp_comments` VALUES (2, 8, 'WordPress先生', '', 'https://wordpress.org/', '', '2016-07-23 08:23:38', '2016-07-23 00:23:38', '您好，这是一条评论。\n要删除评论，请先登录，然后再查看这篇文章的评论。登录后您可以看到编辑或者删除评论的选项。', 0, '1', '', '', 0, 0);
INSERT INTO `wp_comments` VALUES (3, 369, '一位WordPress评论者', 'wapuu@wordpress.example', 'https://wordpress.org/', '', '2017-08-09 17:35:01', '2017-08-09 09:35:01', '嗨，这是一条评论。\n要开始审核、编辑及删除评论，请访问仪表盘的“评论”页面。\n评论者头像来自<a href=\"https://gravatar.com\" rel=\"nofollow\">Gravatar</a>。', 0, '1', '', '', 0, 0);
